mapper.select:
select b.board_id, b.user_seq, u.user_name, u.user_profile_image, b.title, b.content, b.reg_dt, b.read_count
from board b join users u on b.user_seq=u.user_seq
where b.board_id = #{boardId}

mapper.update:
update board
set title = #{title},
    content = #{content}
where board_id = #{boardId}
